<template>  <Layout>    <template #left-container>      <div class="conversation-area">        <div v-for="(message, index) in contact" :key="index" class="msg online" :class="{active: message.receiverEmail === store.activeReceiver}" @click="active(index,message.receiverEmail)">           <img class="msg-profile" :src="message.profileImage" alt="">          <div class="msg-detail">            <div class="msg-username">{{ message.remark }}</div>            <div class="msg-content">              <span class="msg-message">{{ message.lastMsg }}</span>              <!-- <span class="msg-date">{{ message.date }}</span> -->            </div>          </div>        </div>      </div>    </template>    <template #right-container>      <ChatWindow :receiver="receiverEmail" @some-event="updateConversation"/><!--      <div class="chat-area">--><!--        <div class="user-info">{{chatUser}}</div>--><!--        <div class="chat-area-main" @scroll="handleScroll">--><!--          <div--><!--            v-for="(message, index) in messages"--><!--            :key="index"--><!--            class="chat-msg"--><!--            :class="{ owner: message.senderEmail===store.userEmail }"--><!--          >--><!--            <div class="chat-msg-profile">--><!--              <img class="chat-msg-img" :src="message.profileImage" alt="" />--><!--              <div class="chat-msg-date">{{ message.timestamp }}</div>--><!--            </div>--><!--            <div class="chat-msg-content">--><!--              <div class="chat-msg-text">--><!--                {{ message.content }}--><!--              </div>--><!--            </div>--><!--          </div>--><!--        </div>--><!--        <div class="chat-area-footer">--><!--          <el-input--><!--            v-model="textarea1"--><!--            style="width: 240px"--><!--            autosize--><!--            type="textarea"--><!--            placeholder="Please input"--><!--          />--><!--          <el-button type="primary" @click="sendMessage">发送</el-button>--><!--          <el-button type="primary" @click="ws">ws</el-button>--><!--        </div>--><!--      </div>-->    </template>  </Layout></template><script setup>import { onMounted, ref } from 'vue'import ChatWindow from './ChatWindow.vue'import axios from 'axios'import userInfoStore from "../store/UserInfoStore";const store = userInfoStore()const textarea1 = ref('')const contact = ref([])const activeIndex = ref()const chatUser = ref()const receiverEmail = ref('')// for (let i = 0; i < 10; i++) {//   contact.push({//     profileImage: 'https://q1.qlogo.cn/g?b=qq&nk=2029426587&s=640',//     username: 'Madison Jones',//     content: `What time was our meet ${i + 1}`,//     date: `${20 - i}m`//   });// }const messages = ref([])// for (let i = 0; i < 20; i++) {//   messages.push({//     profileImage: i % 2 === 0 ? 'https://q1.qlogo.cn/g?b=qq&nk=2029426587&s=640' : 'https://q1.qlogo.cn/g?b=qq&nk=1520294039&s=640',//     date: `Message seen ${1 + Math.floor(Math.random() * 12)}:${Math.floor(Math.random() * 60)}pm`,//     owner: i % 2 === 1,//     text: '你好',//   })// }const getChatList = async () => {  const response = await axios.get('/api/getChatList', {    params: {      senderEmail: store.userEmail    }  })  return response.data.data}const active = (index,receiver) => {  store.setActiveReceiver(receiver)  activeIndex.value = index;  chatUser.value = receiver;  receiverEmail.value=receiver;  getMessages()}const handleScroll=(event)=> {  const { scrollTop, scrollHeight, clientHeight } = event.target;  console.log(scrollHeight)  if (scrollTop === 0) {    console.log(scrollHeight)  }}const ws = () => {  window.api.loginSuccess(store.token);  getChatList()  console.log('contact:', contact.value)  console.log(messages)}const sendMessage = async () => {  const message = {    senderEmail: store.userEmail,    receiverEmail: receiverEmail.value,    content: textarea1.value,    timestamp: Date.now(),  }  messages.value.push(message)  const messageData = new URLSearchParams()  try {    const response = await axios.post('/api/sendMessage', messageData, {      headers: {        'Content-Type': 'application/x-www-form-urlencoded'      }    })    if (response.status === 200) {      // 请求成功      console.log('Message sent successfully:', response.data)      // 在这里处理成功的逻辑，例如显示成功消息或更新 UI    } else {      // 请求失败      console.error('Error sending message:', response.data)      // 在这里处理失败的逻辑，例如显示错误消息    }  } catch (error) {    // 捕获网络错误或其他未预料到的错误    console.error('Error sending message:', error)    // 在这里处理错误，例如显示错误消息  }}const getMessages = async () => {  const response = await axios.get('/api/getMessageList', {params:{    senderEmail: store.userEmail,      receiverEmail: receiverEmail.value,    }})  messages.value=response.data.data}const updateSingleContact = (message) => {  const conversation = contact.value.find((convo) => convo.receiverEmail === message.senderEmail);  if (conversation) {    console.log(conversation)    conversation.lastMsg = message.content;    // 如果有其他字段需要更新，也可以在这里处理  }}const updateConversation =(email,content)=> {  // Find the index of the conversation to update  const index = contact.value.findIndex((convo) => convo.receiverEmail === email);  console.log(index)  if (index !== -1) {    // Update the conversation at the found index    contact.value[index] = {      ...contact.value[index],      lastMsg: content,      timestamp: Date.now(),    };  }}window.api.messageReceived((event, message) => {  console.log(message)  updateConversation(message.senderEmail,message.content)  //updateSingleContact(message)})onMounted(async () => {  contact.value = await window.api.getChatList(store.userEmail)})</script><style>.wrapper {  width: 100%;  display: flex;  flex-grow: 1;  overflow: hidden;  height: 90vh;}.conversation-area {  max-height: 100%;  border-right: 1px solid var(--border-color);  overflow-y: auto;  overflow-x: hidden;  display: flex;  flex-direction: column;  position: relative;}.msg-profile {  width: 44px;  height: 44px;  border-radius: 50%;  object-fit: cover;  margin-right: 15px;  &.group {    display: flex;    justify-content: center;    align-items: center;    background-color: var(--border-color);    svg {      width: 60%;    }  }}.msg {  display: flex;  align-items: center;  padding: 20px;  cursor: pointer;  transition: 0.2s;  position: relative;  box-sizing: border-box;  &:hover {    background-color: var(--msg-hover-bg);  }  &.active{    background: var(--active-conversation-bg);  }  &.online:before {    content: '';    position: absolute;    background-color: #23be7e;    width: 9px;    height: 9px;    border-radius: 50%;    border: 2px solid var(--theme-bg-color);    left: 50px;    bottom: 19px;  }}.msg-username {  margin-bottom: 4px;  font-weight: 600;  font-size: 15px;}.msg-detail {  overflow: hidden;}.msg-content {  font-weight: 500;  font-size: 13px;  display: flex;}.msg-message {  white-space: nowrap;  overflow: hidden;  text-overflow: ellipsis;  color: var(--msg-message);}.msg-date {  font-size: 14px;  color: var(--msg-date);  margin-left: 3px;  &:before {    content: '•';    margin-right: 2px;  }}.add {  position: sticky;  bottom: 25px;  background-color: var(--theme-color);  width: 60px;  height: 60px;  border: 0;  border-radius: 50%;  background-image: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' fill='none' stroke='white' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='feather feather-plus'%3e%3cpath d='M12 5v14M5 12h14'/%3e%3c/svg%3e");  background-repeat: no-repeat;  background-position: 50%;  background-size: 28px;  box-shadow: 0 0 16px var(--theme-color);  margin: auto auto -55px;  flex-shrink: 0;  z-index: 1;  cursor: pointer;}.overlay {  position: sticky;  bottom: 0;  left: 0;  width: 340px;  flex-shrink: 0;  background: var(--overlay-bg);  height: 80px;}.chat-area {  display: flex;  flex-direction: column;  overflow: auto;  width: 100%;  height: 100%;  &-header {    display: flex;    position: sticky;    top: 0;    left: 0;    z-index: 2;    width: 100%;    align-items: center;    justify-content: space-between;    padding: 20px;    background: var(--chat-header-bg);  }  &-profile {    width: 32px;    border-radius: 50%;    object-fit: cover;  }  &-title {    font-size: 18px;    font-weight: 600;  }  &-main {    flex-grow: 1;  }}.chat-msg-img {  height: 40px;  width: 40px;  border-radius: 50%;  object-fit: cover;}.chat-msg-profile {  flex-shrink: 0;  margin-top: auto;  margin-bottom: -20px;  position: relative;}.chat-msg-date {  position: absolute;  left: calc(100% + 12px);  bottom: 0;  font-size: 12px;  font-weight: 600;  color: var(--msg-date);  white-space: nowrap;}.chat-msg {  display: flex;  padding: 0 20px 45px;  &-content {    margin-left: 12px;    max-width: 70%;    display: flex;    flex-direction: column;    align-items: flex-start;  }  &-text {    background-color: var(--chat-text-bg);    padding: 15px;    border-radius: 20px 20px 20px 0;    line-height: 1.5;    font-size: 14px;    font-weight: 500;    & + & {      margin-top: 10px;    }  }}.chat-msg-text {  color: var(--chat-text-color);}.owner {  flex-direction: row-reverse;  .chat-msg-content {    margin-left: 0;    margin-right: 12px;    align-items: flex-end;  }  .chat-msg-text {    background-color: var(--theme-color);    color: black;    border-radius: 20px 20px 0 20px;  }  .chat-msg-date {    left: auto;    right: calc(100% + 12px);  }}.chat-msg-text img {  max-width: 300px;  width: 100%;}.chat-area-footer {  height: 50px;  display: flex;  border-top: 1px solid var(--border-color);  width: 100%;  padding: 10px 20px;  align-items: center;  background-color: var(--theme-bg-color);  position: sticky;  bottom: 0;}.chat-area-footer svg {  color: var(--settings-icon-color);  width: 20px;  flex-shrink: 0;  cursor: pointer;  &:hover {    color: var(--settings-icon-hover);  }  & + svg {    margin-left: 12px;  }}.chat-area-footer input {  border: none;  color: var(--body-color);  background-color: var(--input-bg);  padding: 12px;  border-radius: 6px;  font-size: 15px;  margin: 0 12px;  width: 100%;  &::placeholder {    color: var(--input-chat-color);  }}.input-area {  width: 100%;}:root {  --body-bg-color: #e5ecef;  --theme-bg-color: #fff;  --settings-icon-hover: #9fa7ac;  --developer-color: #f9fafb;  --input-bg: #f8f8fa;  --input-chat-color: #a2a2a2;  --border-color: #eef2f4;  --body-font: "Manrope", sans-serif;  --body-color: #273346;  --settings-icon-color: #c1c7cd;  --msg-message: #969eaa;  --chat-text-bg: #f1f2f6;  --theme-color: #0086ff;  --msg-date: #c0c7d2;  --button-bg-color: #f0f7ff;  --button-color: var(--theme-color);  --detail-font-color: #919ca2;  --msg-hover-bg: rgba(238, 242, 244, 0.4);  --active-conversation-bg: linear-gradient(    to right,    rgba(238, 242, 244, 0.4) 0%,    rgba(238, 242, 244, 0) 100%  );  --overlay-bg: linear-gradient(    to bottom,    rgba(255, 255, 255, 0) 0%,    rgba(255, 255, 255, 1) 65%,    rgba(255, 255, 255, 1) 100%  );  --chat-header-bg: linear-gradient(    to bottom,    rgba(255, 255, 255, 1) 0%,    rgba(255, 255, 255, 1) 78%,    rgba(255, 255, 255, 0) 100%  );}</style>